import "./App.css";
import React from "react";

import Champion from "./components/Champion";
import Popup from "./components/Popup";

import championServices from "./services/champions";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      champions: {},
      popup: false,
    };
  }
  async componentDidMount() {
    const champions = await championServices.fetchAllChampions();
    this.setState({ champions, popup });
  }
  champClickHandler() {
    this.setState({ champions, popup: !this.state.popup });
  }

  render() {
    return (
      <div className="App">
        <h1>League of Legends</h1>
        <div className="cardCont">
          {Object.entries(this.state.champions).map((v, i) => {
            return (
              <Champion
                data={v}
                key={i}
                clickHandler={this.champClickHandler}
              />
            );
          })}
        </div>
        <Popup display={this.state.popup} />
      </div>
    );
  }
}

export default App;
