import React, { useEffect } from "react";
import championServices from "../../services/champions";

export default function ChampionPage() {
  const [champ, setChamp] = React.useState("");
  const [champData, setChampData] = React.useState({});
  useEffect(() => {
    const address = window.location.href.split("/");
    setChamp(address[address - 1]);
  }, []);
  useEffect(() => {
    const fetchData = async () => {
      const champion = await championServices.fetchChampionByName(champ);
      setChampData(champion);
    };
    fetchData();
  }, []);
  useEffect(() => {
    console.log(champ, champData);
  }, [championData]);
  return (
    <div>
      <h1>This is champion page</h1>
    </div>
  );
}
